/**
 * fis.baidu.com
 */

'use strict';

var exports = module.exports = function(content, file) {
 
    if (content.length < 0) {
        return content;
    }

    var PO = require('node-po-ext');

    var po = PO.parse(content);
    var items = po.items;
    var res = {};
    
    for (var i = 0, len = items.length; i < len; i++) {
        var item = items[i];
        res[item.msgid] = item.msgstr[0];
    }

    content = JSON.stringify(res);

    return content;
};
